    constructor(arg = '0') {
            this.arg = 0
            try {
                this.arg = parseInt(arg)
            } catch (e) {}
            if (!Number.isInteger(this.arg) || this.arg < 0 || this.arg > 50) this.arg = 0
            this.widgetSize = config.widgetFamily
        }
