    async renderHeader(widget, customStyle = true) {
            let _icon = await this.getImage("data:image/png;base64,")
            let _title = "ÊîØ‰ªòÂÆùÁõíÂ≠ê"
            let header = widget.addStack()
            header.centerAlignContent()
            let icon = header.addImage(_icon)
            icon.imageSize = new Size(16, 16)
            icon.cornerRadius = 2
            header.addSpacer(10)
            let title = header.addText(_title)
            if (customStyle) title.textColor = Color.white()
            title.leftAlignText()
            title.textOpacity = 0.8
            title.textColor = Color.white()
            title.font = Font.boldSystemFont(16)
            widget.addSpacer(20)
            return widget
        }
